import React from 'react';
import './discount.css';

const Discount = () => {
    return ( 
        <>
            <div className='container-fluid discount-container'>
                <div className='row discount-row'>
                    <div className="col-xl-6 col-12">
                        <div className='title-discount'>
                            <h1>تخفیف های ویژه چیچیلاسی</h1>
                        </div>
                    </div>
                    <div className="col-xl-6 col-12">
                        <div className='timer-discount'>
                            {
                                deadline()
                            }
                            <div className="second">
                                <p className='num-discount'>06</p>
                                <p className='text-discount'>ثانیه</p>
                            </div><span>:</span>
                            <div className="minute">
                                <p className='num-discount'>32</p>
                                <p className='text-discount'>دقیقه</p>
                            </div><span>:</span>
                            <div className="hour">
                                <p className='num-discount'>14</p>
                                <p className='text-discount'>ساعت</p>
                            </div><span>:</span>
                            <div className="day">
                                <p className='num-discount'>2</p>
                                <p className='text-discount'>روز</p>
                            </div>
                        </div>
                    </div>
                    
                </div>
            </div>
        </>
    );

    function deadline() {
        setInterval(() => {
            
        },1000)
    }
}
 
export default Discount;
