import React , {useState} from "react";
import './filter.css'
import {RiArrowDropDownLine} from 'react-icons/ri'

const Filter = () => {
    const[allProducts,setAllProducts] = useState([
        {name: 'عروسک', items: ['خرسی','دختر بچه','پونی'], isOpen:false},
        {name: 'لباس شخصیت های کارتونی', items: ['مردعنکبوتی','بتمن','هالک'], isOpen:false},
        {name: 'تم تولد', items: ['صورتی','دختر کفشدوزی','نینجا'], isOpen:false},
        {name: 'عروسک دست ساز', items: ['خرسی','دختر بچه','پونی'], isOpen:false},
        {name: 'لباس حیوانات عروسکی', items: ['خرس','خرگوش','شیر'], isOpen:false}
    ])
    let productsAry = [];
    let allProductsCopy = [...allProducts]
    productsAry = allProductsCopy.map((product)=> {
        let productContent = [];
        productContent = product.items.map((item)=>{
            return (<li>{item}</li>)
        })

        return (<>
            <li className="filter-group-list-item" on>
                <div className="filter-group-item-title">{product.name}<div><RiArrowDropDownLine/></div></div>
                <ol className="filter-group-item-contents">
                    {productContent}
                </ol>
            </li>
        </>)
    })

    return ( 
        <>
            <div className="filter-group">
                <ul className="filter-group-list list-unstyled">
                    {productsAry}
                </ul>
            </div>
        </>
    );
}
 
export default Filter;
