import React , { useState }  from "react";
import './slider.css';
import {MdKeyboardArrowRight} from 'react-icons/md'
import {MdKeyboardArrowLeft} from 'react-icons/md'
import background1 from '../../../images/@Wallpaper11261.jpg'
import background2 from '../../../images/@Wallpaper11295.jpg'
import background3 from '../../../images/@Wallpaper11394.jpg'
import background4 from '../../../images/@Wallpaper11463.jpg'
let index = 0
const Slider = () => {
    const [images,setImages] = useState([background1,background2,background3,background4]);
    let [activeImg,setActiveImg] = useState(images[index]);
    let bullets = []
    images.forEach((img,i) => {
        if(i == index){
            bullets.push(<li key={i} className="active-item"></li>)
        }else{
        bullets.push(<li key={i} className="bullet-list-item"></li>)
        }
    })
    // console.log(index);
    return (
        <>
            <div className="slider">
                        {
                            // autoSlider()
                        }
                <div className="slider-container">
                    {/* <div className="img-slider" style={{backgroundImage: `url(${activeImg})`}}>
                    </div> */}
                    {/* <img className="slider-img" src={background1} alt="" /> */}
                    {/* <div className="slider-btns">
                        <button className="btn-left" onClick={prevImage}><MdKeyboardArrowLeft/></button>
                        <button className="btn-right" onClick={nextImage}><MdKeyboardArrowRight/></button>
                    </div> */}
                    {/* <div className="slider-bullet">
                        <ul className="bullet-list">
                            {bullets}
                        </ul>
                    </div> */}
                </div>        
                    <div className="col-12">
                        <div className="row row-baner">
                            <div className="col-md-4 col-12 ">
                                <div className="insta-baner"><p>بنر اینستاگرام</p></div>
                            </div>
                            <div className="col-md-8 col-12">
                                <div className="doll-group" onClick={()=> document.location.pathname = 'products'}><p>دسته بندی عروسک ها</p></div>
                            </div>
                        </div>
                    </div>
            </div>  
        </> 
    );

    function autoSlider(){
        setTimeout(nextImage,6000);
    }

    function nextImage(){
        index++;
        if(index >= images.length){
            index = 0;
        }
        // console.log(index);
        setActiveImg(images[index])
    }
    function prevImage(){
        index--;
        if(index < 0){
            index = images.length - 1;
        }
        console.log(index);
        setActiveImg(images[index])
    }

}
 
export default Slider;
