//   {
//     original: background2,
//   },
//   {
//     original: background3,
//   },
// ];


// let image = document.querySelector('.img-slider')
let index = 0
const Slider = () => {
    const [images,setImages] = useState([background1,background2,background3]);
//     const [activeimg , setActiveImg] = useState(0)
// // style={{backgroundImage: `url(${images[2]})` }}
    setInterval(() => {
        lo
        return (
            <>
                <div className="container-fluid">
                    <div className="row row-slider">
                        <div className="col-12 col-slider">
                            {
                                    index + 1 >= images.length ? (index=0) : (index++)
                                    // console.log(index);
                            }
                            {/* {
                                setInterval(() => console.log(`nextImage : ${index}`),3000)
                            } */}
                            <div className="img-slider" style={{backgroundImage: `url(${images[index]})`}}>
                                <div className="slider-btns">
                                    <button className="btn-left"><MdKeyboardArrowLeft/></button>
                                    <button className="btn-right"><MdKeyboardArrowRight/></button>
                                </div>
                                <div className="slider-counter">
                                    <ul className="counter-list">
                                        <li className="counter-list-item"></li>
                                        <li className="counter-list-item"></li>
                                        <li className="counter-list-item"></li>
                                        <li className="counter-list-item"></li>
                                        <li className="counter-list-item"></li>
                                        <li className="counter-list-item"></li>
                                        <li className="active-item"></li>
                                    </ul>
                                </div>
                            </div>       
                        </div>
                        <div className="col-12">
                            <div className="row row-baner">
                                <div className="col-md-4 col-12 ">
                                    <div className="insta-baner"><p>بنر اینستاگرام</p></div>
                                </div>
                                <div className="col-md-8 col-12">
                                    <div className="doll-group"><p>دسته بندی عروسک ها</p></div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>  
            </> 
        );
    },3000)


    // function nextImage(){
    //     let index = 0;
    //     index++;
    //     if(index > images.length){
    //         index = 0;
    //     }
    //     console.log(index);
    //     return `url(${images[1]})`;
    //     setInterval(console.log(`nextImage : ${index}`),3000)
    // }

}
 
export default Slider;



